// Here we are using object destructuring assignment to pluck off our variables from the props object
// We assign them to their own variable names
export default function NavTabs({ currentPage, handlePageChange }) {
  return (
    <ul className="flex">
      <li className={`mr-4 ${currentPage === 'About' ? 'text-blue-500' : 'text-gray-500'}`}>
        <a
          href="#About"
          onClick={() => handlePageChange('About')}
          className="hover:text-blue-500"
        >
          Home
        </a>
      </li>
      <li className={`mr-4 ${currentPage === 'Projects' ? 'text-blue-500' : 'text-gray-500'}`}>
        <a
          href="#Projects"
          onClick={() => handlePageChange('Projects')}
          className="hover:text-blue-500"
        >
          Projects
        </a>
      </li>
      <li className={`mr-4 ${currentPage === 'Contact' ? 'text-blue-500' : 'text-gray-500'}`}>
        <a
          href="#Contact"
          onClick={() => handlePageChange('Contact')}
          className="hover:text-blue-500"
        >
          Contact Me
        </a>
      </li>
      <li className={`mr-4 ${currentPage === 'Resume' ? 'text-blue-500' : 'text-gray-500'}`}>
        <a
          href="#Resume"
          onClick={() => handlePageChange('Resume')}
          className="hover:text-blue-500"
        >
          Resume
        </a>
      </li>
    </ul>
  );
}




